# -*- coding: utf-8 -*-import foursquareimport pymongofrom pymongo import Connection, GEO2Dimport sysmConn = Connection("217.199.220.182")mCol1 = mConn.foursquare.venues_testmCol2 = mConn.googleplaces.places_testmUnion = mConn.foursquare_googleplaces.poimCol2.ensure_index([("geoPoint", pymongo.GEO2D)])mCol2.ensure_index('lc_name')for iter in mCol2.find(): #{'lc_name': {'$exists': False}}  - пропустить уже добавленные	loc = iter['geometry']['location']	iter['lc_name'] = iter['name'].lower().strip()	iter['geoPoint'] = {'lat': loc['lat'], 'lon': loc['lng']}	mCol2.save(iter)doubles = {}	for iter in mCol1.find():	if iter['location'] and iter['name']:		loc = iter['location']		jiter = mCol2.find_one({'geometry.location': {'$near': {'lat': loc['lat'], 'lng': loc['lng']}, '$maxDistance': 0.001}, 'lc_name': iter['name'].lower().strip()})   #около 200 м 		if jiter:			doubles[jiter['_id']] = True					addFields = []		if iter['categories']:			for cat in  iter['categories']:				addFields.append(cat['name'])					obj = {'name': iter['name'], 'geoPoint': {'lat': loc['lat'], 'lon': loc['lng']}, 'source': 'foursquare', 'sourceId': iter['id'], 'addFields': addFields}		mUnion.save(obj)		for iter in mCol2.find():	if iter['geometry.location'] and iter['name']:		loc = iter['geometry']['location']				if iter['_id'] in doubles:			continue			obj = {'name': iter['name'], 'geoPoint': {'lat': loc['lat'], 'lon': loc['lng']}, 'source': 'googleplaces', 'sourceId': iter['reference'], 'addFields': iter['types']}		mUnion.save(obj)							