<?require_once $_SERVER['DOCUMENT_ROOT'].'/modules/command.php';/*** Список ситибуков*/class Ctbooks{	/**	*	Получить список ситибуков (пока только по url автора)	*/		function get($author)	{		global $db;		$params = array();		if($author) $params['author']=normUrl($author);		return fixMongoId(iterator_to_array($db->ctbooks->find($params, array('_id'=>true, 'title'=>true)), false));		}	/**	*	Удалить все ситибуки	*/		function delete()	{		global $db;		$buf = $db->ctbooks->remove(array(), array('safe'=>true));		return !!$buf['ok'];		}}?>