<?/*** Тестовая команда*/function isAssoc($arr){    return array_keys($arr) !== range(0, count($arr) - 1);}function fixMongoId($obj){	$out = null;	if(is_array($obj))	{		$out = array();		if(isAssoc($obj))		{			foreach($obj as $k=>$v)			{				if($k == '_id')				{					$out['id']=(string)$v;				}else{					$out[$k] = fixMongoId($v);				}			}				}else{			foreach($obj as $v)			{				$out[] = fixMongoId($v);			}			}	}else{		if(is_object($obj) && get_class($obj) == 'MongoId')		{			$out = (string)$obj;		}else{			$out = $obj;		}	}	return $out;}class Poi{	/**	*	получить POI (тестовый id = 519d20099fe5c37c6b00000a )	*/	function get($id)	{		global $db;		return fixMongoId($db->poi->findOne(array('_id' => new MongoId($id))));	}	/*function delete($id)	{		return $db->poi->remove(array('_id' => new MongoId($id)), array("justOne" => true));	}*/		}?>