<?require_once $_SERVER['DOCUMENT_ROOT'].'/modules/command.php';/*** Типы POI*/class Poitypes{	/**	*	получить типы POI	*/	function get($parent)	{		global $db;		if(!$parent) 		{			$parent = null;		}else{			$parent = new MongoId($parent);		}		return iterator_to_array($db->poiTypes_buf->find(array('parent'=>$parent)), false);	}}?>