<?require_once $_SERVER['DOCUMENT_ROOT'].'/modules/command.php';/*** Тегоые ссылки*/class Taglinks{	/**		* Команда редактирования тегов сущности 	*/		function post($url, $tags)	{		if(!$url) error('url не указан');				global $db;		$url = normUrl($url);				if($tags) $tags = json_decode($tags);		$buf = array();		foreach($tags as $tag)		{			$buf[] = stemString($tag);		}				$obj = 	array(			'url'=>$url,			'tags'=>$buf		);				if(!$db->taglinks->save(array('url' => $url), $obj))error('Ошибка записи в базу');			}	/**	*		*/		function get()	{	}		/**	*		*/		function delete()	{	}	}?>