<?require_once $_SERVER['DOCUMENT_ROOT'].'/modules/command.php';/*** Тегоые ссылки*/class Taglinks{	/**		* Команда редактирования тегов сущности 	*/		function post($url, $tags)	{		if(!$url) error('url не указан');				global $db;		$url = normUrl($url);				if($tags) 		{			$tags = json_decode($tags);			$tags = implode(' ', $tags);		}		/*$buf = array();		foreach($tags as $tag)		{			$buf[] = stemString($tag);		}*/				$obj = $db->tagLinks->findOne(array('url'=>$url));		if(!$obj) $obj = array();				$obj['url']=$url;		$obj['tags']=$tags;				if(!$db->tagLinks->save($obj))error('Ошибка записи в базу');			return fixMongoId($obj); 			}	/**	*		*/		function get()	{	}		/**	*		*/		function delete()	{	}	}?>