<?require_once $_SERVER['DOCUMENT_ROOT'].'/modules/command.php';/*** Тегоые ссылки*/class Tagsearch{	/**	*		*/		function get($tags, $urlMask)	{		if($tags) $tags = json_decode($tags);		global $db;						$buf = array();		$hyp = array();		$syn = array();		foreach($tags as $tag)		{			$tag = stemString($tag);//оставляем основу слова			$rec = array($tag);			$rec = array_merge($rec, $tez->getHyponims($tag));//находим гипонимы			$rec = array_merge($rec, $tez->getSynonims($tag));//находим синонимы						$buf[] = $rec;		}				//запрос должен строиться таким образом, чтобы объединять записи buf логикой "И", а слова внутри записи - "ИЛИ" 		//также необхоидмо попробовать встроенный текстовый поиск mongodb 				$db->tagLinks->find(array());			}	}?>